import Router from '@/router.js';
import { apiService } from "../services/api.js";
import ReservationDetail from '@/components/Reservation/ReservationDetail.vue';
export default {
  components: {
    ReservationDetail: ReservationDetail
  },
  data: function data() {
    return {
      reservation_id: 0,
      reservation: null,
      card_number: "",
      cvv: "",
      expiry_date_month: "01",
      expiry_date_year: "24"
    };
  },
  computed: {
    year: function year() {
      return this.reservation.date.getYear() + 1900;
    },
    month: function month() {
      return this.reservation.date.getMonth() + 1;
    },
    day: function day() {
      return this.reservation.date.getDate();
    },
    expiry_date: function expiry_date() {
      return this.expiry_date_month + "/" + this.expiry_date_year;
    },
    arrival_time: function arrival_time() {
      return new Date("2020-01-01 " + this.reservation.arrival_time);
    },
    departure_time: function departure_time() {
      return new Date("2020-01-01 " + this.reservation.departure_time);
    },
    seat_class_name: function seat_class_name() {
      var m = {
        premium: "プレミアム",
        reserved: "普通席",
        "non-reserved": "自由席",
        "": ""
      };
      return m[this.reservation.seat_class];
    }
  },
  methods: {
    getReservation: function getReservation() {
      var _this = this;

      apiService.getReservation(this.reservation_id).then(function (res) {
        _this.reservation = res;
        console.log(res);
      });
    },
    payment: function payment() {
      var _this2 = this;

      var data = {
        card_number: this.card_number,
        cvv: this.cvv,
        expiry_date: this.expiry_date
      };
      apiService.tokenizeCard(data).then(function (res) {
        console.log("OK");
        console.log(res);
        var card_token = res.card_token;
        var data = {
          "reservation_id": _this2.reservation_id,
          "card_token": card_token
        };
        apiService.commit(data).then(function (res) {
          Router.push({
            path: "/"
          });
        });
      });
    }
  },
  mounted: function mounted() {
    this.reservation_id = parseInt(this.$route.query.reservation_id);
    return this.getReservation();
  }
};
