import Router from '@/router.js';
import TrainItem from '@/components/Trains/TrainItem.vue';
import { apiService } from "../services/api.js";
export default {
  data: function data() {
    return {
      year: null,
      month: null,
      day: null,
      hour: null,
      minute: null,
      train_class: "",
      from_station: "",
      to_station: "",
      adult: null,
      child: null,
      position: "",
      selectedItem: null,
      seat_class: "",
      is_smoking_seat: false,
      items: null
    };
  },
  components: {
    TrainItem: TrainItem
  },
  computed: {
    itemCount: function itemCount() {
      return this.items.length;
    },
    condition: function condition() {
      return {
        year: this.year,
        month: this.month,
        day: this.day,
        hour: this.hour,
        minute: this.minute,
        train_class: this.train_class,
        from_station: this.from_station,
        to_station: this.to_station,
        adult: this.adult,
        child: this.child
      };
    },
    canSelectSeat: function canSelectSeat() {
      if (this.seat_class == "" || this.seat_class == "non-reserved") {
        return false;
      }

      return true;
    },
    columnChoices: function columnChoices() {
      if (this.seat_class == "premium") {
        return [{
          name: "指定しない",
          value: ""
        }, {
          name: "窓　側 (A)",
          value: "A"
        }, {
          name: "通路側 (B)",
          value: "B"
        }, {
          name: "通路側 (C)",
          value: "C"
        }, {
          name: "窓　側 (D)",
          value: "D"
        }];
      }

      if (this.seat_class == "reserved") {
        return [{
          name: "指定しない",
          value: ""
        }, {
          name: "窓　側 (A)",
          value: "A"
        }, {
          name: "中　央 (B)",
          value: "B"
        }, {
          name: "通路側 (C)",
          value: "C"
        }, {
          name: "通路側 (D)",
          value: "D"
        }, {
          name: "窓　側 (E)",
          value: "E"
        }];
      }

      return [];
    },
    position_display: function position_display() {
      if (this.position == "") {
        return "指定しない";
      }

      if (this.seat_class == "premium") {
        var m = {
          A: "窓　側 (A)",
          B: "通路側 (B)",
          C: "通路側 (C)",
          D: "窓　側 (D)"
        };
        return m[this.position];
      }

      if (this.seat_class == "reserved") {
        var m = {
          A: "窓　側 (A)",
          B: "中　央 (B)",
          C: "通路側 (C)",
          D: "通路側 (D)",
          E: "窓　側 (E)"
        };
        return m[this.position];
      }

      return "";
    }
  },
  methods: {
    unselect: function unselect() {
      this.selectedItem = null;
    },
    select: function select(item) {
      this.selectedItem = item;
    },
    search: function search() {
      var _this = this;

      apiService.getTrains(this.condition).then(function (res) {
        console.log(res);
        var items = [];
        res.forEach(function (value) {
          value["departure"] = value["departure"];
          value["arrival"] = value["arrival"];
          value["departure_time"] = new Date("2000-01-01 " + value["departure_time"]);
          value["arrival_time"] = new Date("2000-01-01 " + value["arrival_time"]);
          items.push(value);
        });
        _this.items = items;
      });
    },
    selectSeatClass: function selectSeatClass(seat_class, is_smoking_seat) {
      this.seat_class = seat_class;
      this.is_smoking_seat = is_smoking_seat;
    },
    selectSeat: function selectSeat() {
      var query = {
        year: this.year,
        month: this.month,
        day: this.day,
        train_class: this.selectedItem.train_class,
        train_name: this.selectedItem.train_name,
        car_number: 4,
        from_station: this.from_station,
        to_station: this.to_station,
        adult: this.adult,
        child: this.child,
        seat_class: this.seat_class
      };

      if (this.seat_class != "") {
        Router.push({
          path: '/reservation/seats',
          query: query
        });
      }
    },
    reserve: function reserve() {
      if (this.seat_class == "") {
        return;
      }

      var condition = {
        year: this.year,
        month: this.month,
        day: this.day,
        train_class: this.selectedItem.train_class,
        train_name: this.selectedItem.train_name,
        car_number: 0,
        from_station: this.from_station,
        to_station: this.to_station,
        adult: this.adult,
        child: this.child,
        seat_class: this.seat_class,
        is_smoking_seat: this.is_smoking_seat,
        column: this.position,
        seats: []
      };
      apiService.reserve(condition).then(function (res) {
        var query = {
          reservation_id: res.reservation_id
        };
        Router.push({
          path: '/reservation/payment',
          query: query
        });
      });
    }
  },
  mounted: function mounted() {
    this.year = this.$route.query.year;
    this.month = this.$route.query.month;
    this.day = this.$route.query.day;
    this.hour = this.$route.query.hour;
    this.minute = this.$route.query.minute;
    this.train_class = this.$route.query.train_class;
    this.adult = parseInt(this.$route.query.adult);
    this.child = parseInt(this.$route.query.child);
    this.from_station = this.$route.query.from_station;
    this.to_station = this.$route.query.to_station;
    return this.search();
  }
};
