.seat-select {
  background-color: #ddd;
}

.seat-select table {
  width: 80%;
  margin-left: auto;
  margin-right: auto;
  text-align: center;
  table-layout: fixed;
}

.seat-select table thead,
.seat-select table tr {
  height: 40px;
}

.seat-select table th,
.seat-select table td {
  border-collapse: collapse;
  border-color: #ddd;
}

.seat-select table th {
  background-color: #0E5AF5;
  color: #ffffff;
}

.seat-select table tr td {
  color: white;
  background-color: #7BA1F9;
  cursor: pointer;
}

.seat-select table.filled tr td {
  background-color: #AAAAAA;
}

.seat-select table tr td.disabled {
  background-color: #777777;
}

.seat-select table tr td.selected {
  background-color: #EB0000;
}


.reserve {
  width: 300px;
  height: 50px;
  line-height: 50px;
  text-align: center;
  margin-left: auto;
  margin-right: auto;
  margin-top: 20px;
  margin-bottom: 20px;

  color: red;
  background: pink;
  font-size: 20px;
  border-width: 1px;
  border-color: #999999;
  border-top-left-radius: 20px;
  border-bottom-left-radius: 20px;
  border-top-right-radius: 20px;
  border-bottom-right-radius: 20px;
  font-weight: bold;
}


.button-area {
  text-align: center;
}
