.cover {
  position: fixed;
  width: 100%;
  height: 100%;
  background: #000000;
  top: 0px;
  left: 0px;
  opacity: 0.3;
}

.popup {
  width: 460px;
  padding: 30px 0;
  background: #ffffff;
  position: absolute;
  left: 460px;
  top: 0px;
}

.popup .prices {
  margin-left: 20px;
}

.popup .prices .thead.th {
  background: #ffffff;
}

.popup .prices .thead.th,
.popup .prices .thead.td {
  height: 50px;
  text-align: center;
  line-height: 50px;
  border-style: solid;
  border-color: #ffffff;
  margin-bottom: 10px;
  padding: 0;
}

.popup .prices .thead.td.economy {
  background: #1C60EC;
}

.popup .prices .thead.td.green {
  background: #75AC33;
}

.popup .prices .th,
.popup .prices .td {
  float: left;
  margin: 0;
  height: 70px;
  padding: 0;
}

.popup .prices h3 {
  margin: 0;
}

.popup .prices .th {
  width: 227px;
  font-size: 12px;
  line-height: 1.2;
  color: #1D1F86;
  border-width: 1px 9px 1px 0;
  border-style: solid;
  border-color: #ffffff;
  background: #B9E3F8;
}

.popup .prices .td {
    width: 93px;
    font-size: 14px;
    line-height: 1.4;
    color: #ffffff;
    border-width: 1px 1px 1px 0;
    border-style: solid;
    border-color: #ffffff;
    text-align: center;
    height: 60px;
    padding-top: 10px;
}

.popup .prices .td.economy {
  background: #6E98F8;
}

.popup .prices .td.green {
  background: #98C33F;
}


/* ラベルのスタイル　*/
.popup .prices label {
	padding: 0;			/* ラベルの位置 */
	font-size:		10px;
	line-height:		20px;
	display:		inline-block;
	cursor:			pointer;
	position:		absolute;
  margin-top: -8px;
  margin-left: -45px;
}

/* ボックスのスタイル */
.popup .prices label:before {
	content:		'';
	width:			20px;			/* ボックスの横幅 */
	height:			20px;			/* ボックスの縦幅 */
	display:		inline-block;
	position:		absolute;
	left:			0;
	background-color:	#fff;
	box-shadow:		inset 1px 2px 3px 0px #000;
	border-radius:		6px 6px 6px 6px;
}
/* 元のチェックボックスを表示しない */
.popup .prices input[type=radio] {
	display:		none;
}
/* チェックした時のスタイル */
.popup .prices input[type=radio]:checked + label:before {
	content:		'\2713';		/* チェックの文字 */
	font-size:		20px;			/* チェックのサイズ */
	color:			#fff;			/* チェックの色 */
	background-color:	#06f;			/* チェックした時の色 */
}

/* チェックした時のスタイル */
.popup .prices input[type=radio]:disabled + label:before {
	background-color:	#555;			/* チェックした時の色 */
}

.popup .seat {
  margin-top: 10px;
  width: 424px;
  height: 60px;
  line-height: 60px;
  margin-left: 20px;
  text-align: center;
  float: left;
  background: #faae36;
  cursor: pointer;
}

.popup .seat.disabled {
  background: #aaaaaa;
  cursor: default;
}

.popup .continue.disabled {
  background: #aaaaaa;
  color: white;
  cursor: default;
}

.popup .position {
  float: left;
  position: relative;
  display: inline-block;
  margin-left: 20px;
  margin-top: 20px;
  height: 60px;
  line-height: 60px;
  cursor: pointer;
  vertical-align: top;
  color: #ffffff;
  font-size: 24px;
  background: #C9732B;
  width: 200px;
  font-size: 17px;
  text-align: center;
}

.popup .position select {
  position: absolute;
  left: 0px;
  top: 0px;
  height: 50px;
  font-size: 17px;
  opacity: 0;
  z-index: 5;
  cursor: pointer;
  width: 100%;
}


.popup .continue {
  margin-top: 20px;
  width: 204px;
  height: 60px;
  line-height: 60px;
  margin-left: 20px;
  text-align: center;
  float: left;
  background: pink;
  color: #ff0000;
  cursor: pointer;
}


.popup .close {
  margin-top: 20px;
  width: 100px;
  height: 40px;
  line-height: 40px;
  margin-left: 20px;
  text-align: center;
  float: left;
  background: #A0A0A0;
  cursor: pointer;
  color: #ffffff;
}
