div.search {
  background: #FFEAB4;
}

section.search {
  width: 640px;
  margin: 0;
  float: left;
}

.search article {
  position: relative;
  display: inline-block;
  height: 50px;
  cursor: pointer;
  vertical-align: top;
  color: #ffffff;
  font-size: 24px;
}

.search article div {
  padding-top: 10px;
  line-height: 1.2;
  text-align: center;
}

.search select {
  position: absolute;
  left: 0px;
  top: 0px;
  height: 50px;
  font-size: 24px;
  opacity: 0;
  z-index: 5;
  cursor: pointer;
  width: 100%;
}

article.year {
  width: 214px;
  height: 80px;
  font-size: 40px;
  background: #1C1F84;
}


article.month {
  width: 213px;
  height: 80px;
  font-size: 40px;
  background: #1C1F84;
}

article.day {
  width: 213px;
  height: 80px;
  font-size: 40px;
  background: #1C1F84;
}

article.hour {
  width: 250px;
  background: #0057D3;
}


article.minute {
  width: 250px;
  background: #0057D3;
}


article.minute-after {
  width: 140px;
  background: #0057D3;
}

article.train_class {
  background: #1C1F84;
  width: 640px;
}

article.from ,
article.to {
  height: 60px;
  font-size: 30px;
  background: #0057D3;
  width: 300px;
}

article.arrow {
  height: 60px;
  background: #0057D3;
  width: 40px;
}


article.adult ,
article.child {
  background: #1C1F84;
  width: 320px;
}


.subcontent {
  width: 317px;
  float: left;
}

.subcontent article {
    margin: 0;
    padding: 13px 19px;
    width: 270px;
    background:    #FFEAB4;
    height: 200px;
}

.subcontent article.notice h2{
  margin:      0 0 8px 0;
  padding:    0 0 8px 0;
  font-size:    18px;
  color:      #000000;
  line-height:    1.1;
  font-weight:    normal;
  border-bottom:    1px dashed #666666;
}

.subcontent article.notice p{
  margin:      0;
  padding:    0;
  font-size:    13px;
  color:      #666666;
  line-height:    1.4;
}

.button-area {
  text-align:center;
}
button {
  width: 300px;
  height: 50px;
  margin: 30px;
  background: pink;
  color: red;
  font-size: 20px;
  border-width: 1px;
  border-color: #999999;
  border-top-left-radius: 20px;
  border-bottom-left-radius: 20px;
  border-top-right-radius: 20px;
  border-bottom-right-radius: 20px;
  font-weight: bold;
}
